#!/bin/bash
# Legacy MCP Cleanup Script
# Generated by Backend Dev for CLI Reflection Migration

echo "🧹 Starting Legacy MCP Cleanup..."

# 1. FILES TO REMOVE
echo "📁 Removing manual tool directories..."

# Remove tools directory
