mv -f docs/architecture/mcp-design.md .archive/mcp_manual_tools/ 2>/dev/null || true
echo "  ✅ Archived obsolete MCP documentation"

echo "✨ Legacy MCP cleanup complete!"

# CLEANUP SUMMARY
